mapper.select:
SELECT USER_ID,ID, NAME, ADDRESS, ADDRESS2, ZIPCODE, REQUEST_OPTION, REQUEST_TEXT
FROM ADDRESS
WHERE USER_ID = #{user_id}

mapper.update:
UPDATE ADDRESS SET
USER_ID = #{user_id},
NAME = #{name},
ADDRESS = #{address},
ADDRESS2 = #{address2},
ZIPCODE = #{zipcode},
REQUEST_OPTION = #{request_option},
REQUEST_TEXT = #{request_text}
WHERE USER_ID = #{user_id}
AND ID = #{id}

mapper.delete:
DELETE FROM ADDRESS
WHERE USER_ID = #{user_id}
AND ID = #{id}

DELETE FROM REVIEW
WHERE USER_ID = #{user_id}
AND ID = #{id}
